<template>  <el-button-group>    <el-button size="small" plain @click="confirmAction"      ><i class="el-icon-check button-set_success" /> Готово</el-button    >    <el-button size="small" plain @click="closeAction"      ><i class="el-icon-close button-set_danger" /> Отмена</el-button    >    <el-button size="small" plain @click="editAction" icon="el-icon-more"      >Изменить</el-button    >  </el-button-group></template><script>  export default {    name: "ButtonGroupApp",    props: {      confirm: String,      close: String,      edit: String,    },    methods: {      confirmAction() {        this.$emit(this.confirm);      },      closeAction() {        this.$emit(this.close);      },      editAction() {        this.$emit(this.edit);      },    },  };</script><style lang="scss">  .button-set_success {    color: $main-green;  }  .button-set_danger {    color: $main-danger;  }</style>